-- Suppression des modules et fonctionnalités désactivées de FES
DELETE FROM FRED_MODULE_DESACTIVE
WHERE SocieteId in (select s.SocieteId from FRED_SOCIETE s inner join FRED_GROUPE g on s.GroupeId=g.GroupeId where g.Code='GFES')

DELETE FROM FRED_FONCTIONNALITE_DESACTIVE
WHERE SocieteId in (select s.SocieteId from FRED_SOCIETE s inner join FRED_GROUPE g on s.GroupeId=g.GroupeId where g.Code='GFES')

